jQuery(document).ready(function($){var current_gwen_upload=0;var frame=wp.media({title:"Upload or Select an Image",multiple:false,library:{type:"image"},button:{text:"Insert Image"}});gwen_open_uploader=function(id){current_gwen_upload=id;frame.open();return false};frame.on("close",function(){var attachment=frame.state().get("selection").first().toJSON();gwen_render_image(attachment)});gwen_render_image=function(attachment){var imageElem=$("#"+current_gwen_upload+"_image");imageElem.removeAttr("srcset").removeAttr("sizes").attr("src",attachment.url).attr("width",attachment.width).attr("height",attachment.height).css({"max-width":"100%",height:"auto"}).show();$("#"+current_gwen_upload).val(attachment.id);$("#"+current_gwen_upload+"_remove").show()};gwen_clear_uploader=function(current_gwen_upload){$("#"+current_gwen_upload+"_image").attr("src","").hide();$("#"+current_gwen_upload).val("");$("#"+current_gwen_upload+"_remove").hide();return false}});